-- ----------------------------
-- Table structure for article
-- ----------------------------
DROP TABLE IF EXISTS `article`;
CREATE TABLE `article` (
  `aid` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(150) NOT NULL DEFAULT '' COMMENT '文章标题',
  `click` mediumint(8) unsigned NOT NULL DEFAULT '0' COMMENT '点击次数',
  `sendtime` int(20) unsigned NOT NULL DEFAULT '0' COMMENT '发布时间',
  `update_time` int(20) unsigned NOT NULL DEFAULT '0' COMMENT '修改时间',
  `thumb` varchar(255) NOT NULL DEFAULT '' COMMENT '缩略图',
  `digest` varchar(255) NOT NULL DEFAULT '' COMMENT '描述',
  `author` varchar(20) NOT NULL DEFAULT '' COMMENT '作者',
  `user_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '所属用户id',
  `category_cid` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '所属分类cid',
  `is_recycle` smallint(1) unsigned DEFAULT '1',
  PRIMARY KEY (`aid`)
) ENGINE=InnoDB AUTO_INCREMENT=24 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of article
-- ----------------------------
INSERT INTO `article` VALUES ('8', '谁的青春不迷茫', '145', '1483889224', '0', 'article/2017-01-08/14838892241444987870.jpg', '曾谈过一段恋爱，分手理由是因为我不够有钱。后来我拼命赚钱，却再也没有遇见过那个人。曾被同事排挤，因为我不懂规矩。后来我懂了规矩，但再也不会用这个理由去刁难新同事。我一直和父母抗争，因为他们一直觉得我不那么好。后来我过得越来越好，我才知道他们只是怕我一个人过得不好。那些年，我一直在试着了解：了解这个世界，了解更完整的自己。', 'hj', '1', '4', '1');
INSERT INTO `article` VALUES ('9', 'LNMP环境之Nginx--01安装', '9', '1483941263', '0', 'article/2017-01-09/14839412631944112760.jpg', '温故而知新，遗漏之处要补上。', 'hj', '4', '3', '1');
