: 0px; padding: 0px;&quot;/&gt;&amp;lt;!--&amp;lt;script src=&amp;quot;http://static.geetest.com/static/tools/gt.js&amp;quot;&amp;gt;&amp;lt;/script&amp;gt;--&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;lt;script&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; var roots = &amp;quot;{$roots}&amp;quot;;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; var handlerPopup = function (captchaObj) {&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;$(&amp;quot;#popup-submit&amp;quot;).click(function () {&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; var validate = captchaObj.getValidate();&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; if (!validate) {&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;layer.msg(&amp;#39;请先完成验证.^_^&amp;#39;, {&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; icon: 6,&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; time: 2000&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;});&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;return;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; }&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $.ajax({&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;url: roots + &amp;nbsp;&amp;quot;/Home/Index/ajaxcheckCode&amp;quot;, // 进行二次验证&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;type: &amp;quot;post&amp;quot;,&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;// dataType: &amp;quot;json&amp;quot;,&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;data: {&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; // 二次验证所需的三个值&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; geetest_challenge: validate.geetest_challenge,&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; geetest_validate: validate.geetest_validate,&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; geetest_seccode: validate.geetest_seccode&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;},&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;//请求成功，回调处理&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;success: function (result) {&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; if (result.status) {&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;layer.msg(result.msg, {icon: 6,time: 2000},function(){&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; window.location.href = result.url;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;});&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; } else {&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;layer.msg(result.msg, {icon: 6,time: 2000});&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; }&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;}&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; });&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;});&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;// 弹出式需要绑定触发验证码弹出按钮&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;captchaObj.bindOn(&amp;quot;#popup-submit&amp;quot;);&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;// 将验证码加到id为captcha的元素里&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;captchaObj.appendTo(&amp;quot;#popup-captcha&amp;quot;);&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;// 更多接口参考：http://www.geetest.com/install/sections/idx-client-sdk.html&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; };&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; $.ajax({&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;// 获取id，challenge，success（是否启用failback）&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;url: roots + &amp;quot;/Home/Index/ajaxShowCode/t/&amp;quot; + (new Date()).getTime(), // 加随机数防止缓存&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;type: &amp;quot;get&amp;quot;,&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;dataType: &amp;quot;json&amp;quot;,&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;success: function (data) {&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; // 使用initGeetest接口&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; // 参数1：配置参数&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; // 参数2：回调，回调的第一个参数验证码对象，之后可以使用它做appendTo之类的事件&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; initGeetest({&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;gt: data.gt,&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;challenge: data.challenge,&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;product: &amp;quot;popup&amp;quot;, // 产品形式，包括：float，embed，popup。注意只对PC版验证码有效&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;offline: !data.success // 表示用户后台检测极验服务器是否宕机，一般不需要关注&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; }, handlerPopup);&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;}&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; });&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;lt;/script&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;lt;br&amp;gt;&amp;lt;br&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;lt;hr&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;lt;br&amp;gt;&amp;lt;br&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;lt;form class=&amp;quot;popup&amp;quot; action=&amp;quot;/Home/Index/postCheckCode&amp;quot; method=&amp;quot;post&amp;quot;&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;lt;h2&amp;gt;嵌入式Demo，使用&amp;lt;span style=&amp;quot;color: #00be67&amp;quot;&amp;gt;POST表单形式提交&amp;lt;/span&amp;gt;二次验证所需的验证结果值&amp;lt;/h2&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;lt;br&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;lt;p&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;lt;label for=&amp;quot;user&amp;quot;&amp;gt;用户名：&amp;lt;/label&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;lt;input class=&amp;quot;inp&amp;quot; id=&amp;quot;user&amp;quot; type=&amp;quot;text&amp;quot; value=&amp;quot;cocolait博客&amp;quot;&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;lt;/p&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;lt;br&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;lt;p&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;lt;label for=&amp;quot;password&amp;quot;&amp;gt;密&amp;amp;nbsp;&amp;amp;nbsp;&amp;amp;nbsp;&amp;amp;nbsp;码：&amp;lt;/label&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;&amp;lt;input class=&amp;quot;inp&amp;quot; id=&amp;quot;password&amp;quot; type=&amp;quot;password&amp;quot; value=&amp;quot;123456&amp;quot;&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;lt;/p&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;lt;div id=&amp;quot;embed-captcha&amp;quot;&amp;gt;&amp;lt;/div&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;lt;p id=&amp;quot;wait&amp;quot; class=&amp;quot;show&amp;quot;&amp;gt;正在加载验证码......&amp;lt;/p&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;lt;p id=&amp;quot;notice&amp;quot; class=&amp;quot;hide&amp;quot;&amp;gt;请先拖动验证码到相应位置&amp;lt;/p&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;lt;br&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;lt;input class=&amp;quot;btn&amp;quot; id=&amp;quot;embed-submit&amp;quot; type=&amp;quot;submit&amp;quot; value=&amp;quot;提交&amp;quot;&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;lt;/form&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;lt;script&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; var handlerEmbed = function (captchaObj) {&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;$(&amp;quot;#embed-submit&amp;quot;).click(function (e) {&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; //未拖动时 检测&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; var validate = captchaObj.getValidate();&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; if (!validate) {&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;$(&amp;quot;#notice&amp;quot;)[0].className = &amp;quot;show&amp;quot;;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;setTimeout(function () {&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $(&amp;quot;#notice&amp;quot;)[0].className = &amp;quot;hide&amp;quot;;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;}, 2000);&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;e.preventDefault();&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; }&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;});&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;// 将验证码加到id为captcha的元素里&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;captchaObj.appendTo(&amp;quot;#embed-captcha&amp;quot;);&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;captchaObj.onReady(function () {&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; $(&amp;quot;#wait&amp;quot;)[0].className = &amp;quot;hide&amp;quot;;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;});&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;// 更多接口参考：http://www.geetest.com/install/sections/idx-client-sdk.html&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; };&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; $.ajax({&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;// 获取id，challenge，success（是否启用failback）&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;url: roots + &amp;nbsp;&amp;quot;/Home/Index/ajaxShowCode/t/&amp;quot; + (new Date()).getTime(), // 加随机数防止缓存&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;type: &amp;quot;get&amp;quot;,&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;dataType: &amp;quot;json&amp;quot;,&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;success: function (data) {&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; // 使用initGeetest接口&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; // 参数1：配置参数&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; // 参数2：回调，回调的第一个参数验证码对象，之后可以使用它做appendTo之类的事件&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; initGeetest({&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;gt: data.gt,&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;challenge: data.challenge,&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;product: &amp;quot;float&amp;quot;, // 产品形式，包括：float，embed，popup。注意只对PC版验证码有效&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;offline: !data.success // 表示用户后台检测极验服务器是否宕机，一般不需要关注&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; }, handlerEmbed);&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; &amp;nbsp; &amp;nbsp;}&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt; &amp;nbsp; });&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;lt;/script&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;lt;/body&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;lt;/html&amp;gt;&lt;br/&gt;&lt;/span&gt;&lt;/pre&gt;', 'thinkPHP3.2.3', 'thinkPHP3.2.3', '16');
INSERT INTO `article_data` VALUES ('&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;好多时候我们是希望能设置input只能输入数字；比如说商城选择商品数量；比如说手机号等等；&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;看了下京东和天猫；&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;天猫选择商品数量是只能输入数字；而且数量不能小于1；&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img alt=&quot;白俊遥博客&quot; src=&quot;/Upload/ueditor/image/20170109/1483943287666347.png&quot; title=&quot;白俊遥博客&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: none; vertical-align: middle;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;反观京东；连中文都是可以输入的；不得不说；细节上做的比天猫差远了；&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img alt=&quot;白俊遥博客&quot; src=&quot;/Upload/ueditor/image/20170109/1483943287261762.png&quot; title=&quot;白俊遥博客&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: none; vertical-align: middle;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;当然重点不是要来吐槽；下面就是用Js控制input只允许输入数字的代码；在用到的时候可以直接拿走；不用谢；&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;代码如下：&lt;/p&gt;&lt;div class=&quot;line number1 index0 alt2&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; font-size: 17.5px; line-height: 25px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;code class=&quot;html plain&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 15.75px; color: rgb(199, 37, 78); border-radius: 4px; background-color: rgb(249, 242, 244);&quot;&gt;此input只允许输入0-9的数字：&amp;lt;&lt;/code&gt;&lt;code class=&quot;html keyword&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 15.75px; color: rgb(199, 37, 78); border-radius: 4px; background-color: rgb(249, 242, 244);&quot;&gt;input&lt;/code&gt;&amp;nbsp;&lt;code class=&quot;html color1&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 15.75px; color: rgb(199, 37, 78); border-radius: 4px; background-color: rgb(249, 242, 244);&quot;&gt;onkeyup&lt;/code&gt;&lt;code class=&quot;html plain&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 15.75px; color: rgb(199, 37, 78); border-radius: 4px; background-color: rgb(249, 242, 244);&quot;&gt;=&lt;/code&gt;&lt;code class=&quot;html string&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 15.75px; color: rgb(199, 37, 78); border-radius: 4px; background-color: rgb(249, 242, 244);&quot;&gt;&amp;quot;if(event.keyCode&amp;nbsp;!=37&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;event.keyCode&amp;nbsp;!=&amp;nbsp;39)value=value.replace(/\\D/g,&amp;#39;&amp;#39;)&amp;quot;&lt;/code&gt;&lt;code class=&quot;html plain&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 15.75px; color: rgb(199, 37, 78); border-radius: 4px; background-color: rgb(249, 242, 244);&quot;&gt;&amp;gt;&amp;lt;&lt;/code&gt;&lt;code class=&quot;html keyword&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 15.75px; color: rgb(199, 37, 78); border-radius: 4px; background-color: rgb(249, 242, 244);&quot;&gt;br&lt;/code&gt;&lt;code class=&quot;html plain&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 15.75px; color: rgb(199, 37, 78); border-radius: 4px; background-color: rgb(249, 242, 244);&quot;&gt;&amp;gt;&amp;lt;&lt;/code&gt;&lt;code class=&quot;html keyword&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 15.75px; color: rgb(199, 37, 78); border-radius: 4px; background-color: rgb(249, 242, 244);&quot;&gt;br&lt;/code&gt;&lt;code class=&quot;html plain&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 15.75px; color: rgb(199, 37, 78); border-radius: 4px; background-color: rgb(249, 242, 244);&quot;&gt;&amp;gt;&lt;/code&gt;&lt;/div&gt;&lt;div class=&quot;line number2 index1 alt1&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; font-size: 17.5px; line-height: 25px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;code class=&quot;html plain&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 15.75px; color: rgb(199, 37, 78); border-radius: 4px; background-color: rgb(249, 242, 244);&quot;&gt;此input只允许输入1-9的数字：&amp;lt;&lt;/code&gt;&lt;code class=&quot;html keyword&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 15.75px; color: rgb(199, 37, 78); border-radius: 4px; background-color: rgb(249, 242, 244);&quot;&gt;input&lt;/code&gt;&amp;nbsp;&lt;code class=&quot;html color1&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 15.75px; color: rgb(199, 37, 78); border-radius: 4px; background-color: rgb(249, 242, 244);&quot;&gt;onkeyup&lt;/code&gt;&lt;code class=&quot;html plain&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 15.75px; color: rgb(199, 37, 78); border-radius: 4px; background-color: rgb(249, 242, 244);&quot;&gt;=&lt;/code&gt;&lt;code class=&quot;html string&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 15.75px; color: rgb(199, 37, 78); border-radius: 4px; background-color: rgb(249, 242, 244);&quot;&gt;&amp;quot;if(event.keyCode&amp;nbsp;!=37&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;event.keyCode&amp;nbsp;!=&amp;nbsp;39)value=value.replace(/\\D/g,&amp;#39;&amp;#39;);if(value==0)value=1;&amp;quot;&lt;/code&gt;&amp;nbsp;&lt;code class=&quot;html color1&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 15.75px; color: rgb(199, 37, 78); border-radius: 4px; background-color: rgb(249, 242, 244);&quot;&gt;value&lt;/code&gt;&lt;code class=&quot;html plain&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 15.75px; color: rgb(199, 37, 78); border-radius: 4px; background-color: rgb(249, 242, 244);&quot;&gt;=&lt;/code&gt;&lt;code class=&quot;html string&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 15.75px; color: rgb(199, 37, 78); border-radius: 4px; background-color: rgb(249, 242, 244);&quot;&gt;&amp;quot;1&amp;quot;&lt;/code&gt;&lt;code class=&quot;html plain&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 2px 4px; font-family: Menlo, Monaco, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 15.75px; color: rgb(199, 37, 78); border-radius: 4px; background-color: rgb(249, 242, 244);&quot;&gt;&amp;gt;&lt;/code&gt;&lt;/div&gt;', 'javascript', 'javascript', '17');
INSERT INTO `article_data` VALUES ('&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;我们在开发项目中，经常有ajax请求处理操作，不管请求成功或失败，是不是都得有个提示，所以今天我就给大家介绍一个基于Jquery的弹窗插件，layer.js,非常好用，可能是我目前开发用过最好的弹窗插件了，好了那我废话不多说，直接切入主题啦.^_^&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px;&quot;&gt;1.引入js文件&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px;&quot;&gt;&amp;lt;script type=&amp;quot;text/javascript&amp;quot; src=&amp;quot;jquery-1.8.3.min.js&amp;quot;&amp;gt;&amp;lt;/script&amp;gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;lt;script type=&amp;quot;text/javascript&amp;quot; src=&amp;quot;layer/layer.js&amp;quot;&amp;gt;&amp;lt;/script&amp;gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px;&quot;&gt;我用的是 Jquery 1.8.3的 ，官方说最好引入1.8以上的版本，虽然 1.7.2也能使用（本人亲测），最好还是按官方说的吧.^_^&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px;&quot;&gt;2.简单使用案例&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px;&quot;&gt;$(function(){&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;$(&amp;quot;#test1&amp;quot;).click(function(){&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;var _this = $(this);&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;//弹框&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; layer.alert(&amp;#39;加了个图标&amp;#39;, {icon: 1},function(index){&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;layer.close(index);&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;});&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; layer.confirm(&amp;#39;is not?&amp;#39;, {icon: 3, title:&amp;#39;提示&amp;#39;}, function(index){&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;//点击确认才能回调&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp; &amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;layer.close(index);&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;});&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;// confirm确认提示&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; layer.confirm(&amp;#39;is not?&amp;#39;, function(index){&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;//点击确认才能回调&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;});&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;// 信息提示&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;// 1：勾勾 2：叉叉 3：问号 4：锁 5 失败 表情 哭&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;// 6 成功 表情 笑脸&amp;nbsp; 哭脸 7：危险信号&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;// layer.msg(&amp;#39;有表情地提示&amp;#39;, {icon: 4});&amp;nbsp;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; layer.msg(&amp;#39;有表情地提示&amp;#39;, {&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;icon: 6,&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;time: 2000 //2秒关闭（如果不配置，默认是3秒）&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;}, function(){&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;//成功后 回调&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;alert(&amp;#39;我要跳转....&amp;#39;);&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;});&amp;nbsp;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;//加载 0-2 不同的样式 默认为0&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp; layer.load(1);&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; layer.load(2, {time: 10*1000}); //又换了种风格，并且设定最长等待10秒&amp;nbsp;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; //关闭&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; layer.close(index);&amp;nbsp;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;//tips层 4个方向 tips:1 上面 其他的好像还没测试成功 默认是在右边&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; layer.tips(&amp;#39;只想提示地精准些&amp;#39;, _this);&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp; layer.tips(&amp;#39;只想提示地精准些&amp;#39;, _this,{tips:4});&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;});&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;});&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px;&quot;&gt;3.具体项目中运用案例&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px;&quot;&gt;&amp;nbsp;&amp;nbsp; $(&amp;quot;#ajaxSendAllocation&amp;quot;).submit(function(){&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; //layer 加载层&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; var loadColse = layer.load(2, {time: 5*1000});&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; var datas = $(&amp;quot;#ajaxSendAllocation&amp;quot;).serialize();&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $.post(&amp;#39;/Admin/Auth/ajaxSendAllocation&amp;#39;,datas,function(data){&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; layer.close(loadColse);&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; if (data.status) {&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; //layer 信息提示层&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; layer.msg(data.msg,{icon:6,time:2000},function(){&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;window.location.href = data.url;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;});&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; } else {&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; layer.msg(data.msg,{icon:5,time:2000});&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; }&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; },&amp;#39;json&amp;#39;);&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; //阻止表单提交&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; return false;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; });&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px;&quot;&gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(122, 122, 67);&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px;&quot;&gt;详细运用请参考官方&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px;&quot;&gt;&lt;a _src=&quot;http://layer.layui.com/&quot; href=&quot;http://layer.layui.com/&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; color: rgb(51, 122, 183); text-decoration: none; background-color: transparent;&quot;&gt;http://layer.layui.com/&lt;/a&gt;&amp;nbsp;这是layer官方网站，想要研究可以自行去官网了解&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px;&quot;&gt;好了教程就到了.^_^&lt;/p&gt;', 'jQuery弹出层插件', 'jQuery弹出层插件', '18');
INSERT INTO `article_data` VALUES ('&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;&gt;一、导入导出示例&lt;/strong&gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;&gt;&amp;nbsp;1、导出csv文件&amp;nbsp;&lt;/strong&gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot; style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 9.5px; overflow: auto; font-family: Menlo, Monaco, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 13px; line-height: 1.42857; color: rgb(51, 51, 51); word-break: break-all; word-wrap: break-word; border: 1px solid rgb(204, 204, 204); border-radius: 4px; background-color: rgb(245, 245, 245);&quot;&gt;SELECT\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;*\r\nFROM\r\n&amp;nbsp;表名\r\nWHERE\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;id&amp;nbsp;&amp;lt;&amp;nbsp;3000001\r\nINTO&amp;nbsp;OUTFILE&amp;nbsp;&amp;#39;/tmp/data_3000000.csv&amp;#39;\r\nFIELDS\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;TERMINATED&amp;nbsp;BY&amp;nbsp;&amp;#39;,&amp;#39;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;OPTIONALLY&amp;nbsp;&amp;nbsp;ENCLOSED&amp;nbsp;BY&amp;nbsp;&amp;#39;&amp;quot;&amp;#39;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ESCAPED&amp;nbsp;BY&amp;nbsp;&amp;#39;&amp;quot;&amp;#39;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;LINES&amp;nbsp;TERMINATED&amp;nbsp;BY&amp;nbsp;&amp;#39;\\r\\n&amp;#39;;&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;&gt;&amp;nbsp;2、导入csv文件&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot; style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 9.5px; overflow: auto; font-family: Menlo, Monaco, Consolas, &amp;#39;Courier New&amp;#39;, monospace; font-size: 13px; line-height: 1.42857; color: rgb(51, 51, 51); word-break: break-all; word-wrap: break-word; border: 1px solid rgb(204, 204, 204); border-radius: 4px; background-color: rgb(245, 245, 245);&quot;&gt;LOAD&amp;nbsp;DATA\r\nINFILE&amp;nbsp;&amp;#39;/tmp/saledata_3000000.csv&amp;#39;\r\nINTO&amp;nbsp;TABLE&amp;nbsp;cn_data.saledata\r\nCHARACTER&amp;nbsp;SET&amp;nbsp;utf8\r\nFIELDS\r\n&amp;nbsp;&amp;nbsp;TERMINATED&amp;nbsp;BY&amp;nbsp;&amp;#39;,&amp;#39;\r\n&amp;nbsp;&amp;nbsp;OPTIONALLY&amp;nbsp;&amp;nbsp;ENCLOSED&amp;nbsp;BY&amp;nbsp;&amp;#39;&amp;quot;&amp;#39;\r\n&amp;nbsp;&amp;nbsp;ESCAPED&amp;nbsp;BY&amp;nbsp;&amp;#39;&amp;quot;&amp;#39;\r\n&amp;nbsp;&amp;nbsp;LINES&amp;nbsp;TERMINATED&amp;nbsp;BY&amp;nbsp;&amp;#39;\\r\\n&amp;#39;;&lt;/pre&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;&gt;二、参数释义&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;&gt;&amp;nbsp;&lt;/strong&gt;&lt;strong style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;&gt;CHARACTER SET utf8&amp;nbsp;&lt;/strong&gt;&amp;nbsp;\\\\&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体;&quot;&gt;设置编码；&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;strong style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;&gt;FIELDS TERMINATED BY ‘&lt;/strong&gt;&lt;strong style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体;&quot;&gt;字符串&lt;/span&gt;’&lt;/strong&gt;&amp;nbsp;&amp;nbsp;\\\\&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体;&quot;&gt;字符串与字段之间的分隔标识符，可为单个或多个字符；默认为&lt;/span&gt;“\\t”&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体;&quot;&gt;。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;&gt;&amp;nbsp;&lt;/strong&gt;&lt;strong style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;&gt;FIELDS OPTIONALLY ENCLOSED BY ‘&lt;/strong&gt;&lt;strong style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体;&quot;&gt;字符&lt;/span&gt;’&lt;/strong&gt;&amp;nbsp; \\\\&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体;&quot;&gt;设置字符来括住&lt;/span&gt;char varchar text&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体;&quot;&gt;等字符型字段，默认不使用任何符号。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-size: 16px;&quot;&gt;&lt;strong style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: Calibri, sans-serif;&quot;&gt;FIELDS ENCLOSED BY ‘&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体;&quot;&gt;字符&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: Calibri, sans-serif;&quot;&gt;’&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;strong style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: Calibri, sans-serif;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: Calibri, sans-serif;&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; \\\\&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体;&quot;&gt;设置字符来括住字段的值，只能为单个字符；默认不使用任何符号，例如：&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: Calibri, sans-serif;&quot;&gt;NULL&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体;&quot;&gt;字段导出为&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: Calibri, sans-serif;&quot;&gt;NULL&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体;&quot;&gt;。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-size: 16px;&quot;&gt;&lt;strong style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: Calibri, sans-serif;&quot;&gt;FIELDS ESCAPED BY ‘字符’&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&amp;nbsp;&amp;nbsp;\\\\&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体;&quot;&gt;设置转义字符，只能为单个字符；默认为&lt;/span&gt;&amp;nbsp;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体;&quot;&gt;“&lt;/span&gt;\\&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体;&quot;&gt;”。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-size: 16px;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-size: 16px;&quot;&gt;&lt;strong style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: Calibri, sans-serif;&quot;&gt;LINES TERMINATED BY ‘字符串’&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: Calibri, sans-serif;&quot;&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp;\\\\&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体;&quot;&gt;设置每行数据结尾的字符，可以为单个或多个字符；默认为&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: Calibri, sans-serif;&quot;&gt;“\\n”&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体;&quot;&gt;。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体; font-size: 16px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-size: 16px;&quot;&gt;&lt;strong style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: Calibri, sans-serif;&quot;&gt;LINES STARTING BY ‘字符串’&amp;nbsp;&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: Calibri, sans-serif;&quot;&gt;&amp;nbsp;&amp;nbsp; &amp;nbsp; &amp;nbsp; \\\\&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;&gt;设置每行数据开头的字符，可以为单个或多个字符；默认不使用任何字符。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;&gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-size: 16px;&quot;&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; font-family: 宋体; background-color: yellow;&quot;&gt;注意：导出时目标目录必须有写入权限，而且目标文件不能存在。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;', 'MySQL导入导出CSV文件', 'MySQL导入导出CSV文件', '19');
INSERT INTO `article_data` VALUES ('&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; white-space: normal; widows: auto; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); font-family: &amp;#39;Helvetica Neue&amp;#39;, Helvetica, &amp;#39;Hiragino Sans GB&amp;#39;, &amp;#39;Microsoft YaHei&amp;#39;, Arial, sans-serif; line-height: 2em; box-sizing: border-box !important; word-wrap: break-word !important; background-color: rgb(255, 255, 255);&quot;&gt;非诚勿扰里乐嘉老师曾发过一段微博，大意是说自己初恋的时候，有一个姑娘很爱他，那时候他人又穷，又桀骜，又倔又愤，就觉得你爱我你就应该怎样怎样的证明， 最后有一天，姑娘终于跑掉了，确实跟了个有钱人，于是他就又觉得姑娘是因为嫌贫爱富，着实的去跟姑娘纠缠闹腾了一阵子。直到很多年后的今天，自己也成熟 了，有钱有地位了，可以心平气和的想这段往事了，才真的承认，人家姑娘当初跟他分手，不是嫌他穷。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; widows: auto; max-width: 100%; clear: both; min-height: 1em; white-space: pre-wrap; color: rgb(62, 62, 62); font-family: &amp;#39;Helvetica Neue&amp;#39;, Helvetica, &amp;#39;Hiragino Sans GB&amp;#39;, &amp;#39;Microsoft YaHei&amp;#39;, Arial, sans-serif; line-height: 25.6px; box-sizing: border-box !important; word-wrap: break-word !important; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br style=&quot;margin: 0px; padding: 0px; max-width: 100%; white-space: normal; box-sizing: border-box !important; word-wrap: break-word !important;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; white-space: normal; widows: auto; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); font-family: &amp;#39;Helvetica Neue&amp;#39;, Helvetica, &amp;#39;Hiragino Sans GB&amp;#39;, &amp;#39;Microsoft YaHei&amp;#39;, Arial, sans-serif; line-height: 2em; box-sizing: border-box !important; word-wrap: break-word !important; background-color: rgb(255, 255, 255);&quot;&gt;年 少的时候常常想能开一辆敞篷车又带着自己喜欢的人在满是落叶的山路上慢慢开，可是现在我发现这是很难的。因为首先开着敞篷车的时候旁边没有自己喜欢的姑 娘，而有自己喜欢的姑娘在边上的时候又没敞篷车，有敞篷车和自己喜欢的姑娘的时候偏偏又只能被堵车在城里。然后随着时间过去，这样的冲动也越来越少，不像 上学的时候，觉得可以为一个姑娘付出一切 —— 对了，甚至还有生命。&lt;br style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;&quot;/&gt;&lt;br style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;&quot;/&gt;我 只是觉得，什么都变得现实了，无论男女，可能纯真的爱情真的只能在学校里发生，我想，也只有在学校里才能不计较交通工具是一辆自行车，并且还是无铃。也只 能在高中大学里，可能会不计较男友贫富，好看就行，在寝室的室友面前也显得很有面子，但是一出大学，看见你昔日的鸟朋友们都是宾士来接的，或者办公室微微 有姿色的女友们都是名车接送，可能车的牌子不尽相同，但是相同的是车里的人都很难看，这时候你是否会对你骑自行车宾士过来的男朋友依然像以前一样？&amp;nbsp;&lt;br style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;&quot;/&gt;&lt;br style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;&quot;/&gt;有 一篇文章《那个和你一起吃路边摊的姑娘，为什么没有陪你走到最后》，讲了一个故事，主角是D小姐和S君，认识S君和D小姐的时候，他们还没有谈恋爱。那时 候S君和D小姐在一个办公室，渐渐地爱上了，也不做声，每天在网上没事儿人似的聊天。我和S君说话比较多，有一天他半夜告知我此事，这两个都是低调，单纯 而朴实，我心中喜欢的好孩子。听到这个消息，特别为他们高兴。我还记得S君当时可爱的样子，这是两个人的初恋。&lt;br style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;&quot;/&gt;&lt;br style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; white-space: normal; widows: auto; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); font-family: &amp;#39;Helvetica Neue&amp;#39;, Helvetica, &amp;#39;Hiragino Sans GB&amp;#39;, &amp;#39;Microsoft YaHei&amp;#39;, Arial, sans-serif; line-height: 2em; box-sizing: border-box !important; word-wrap: break-word !important; background-color: rgb(255, 255, 255);&quot;&gt;后 来一段工作结束了，S君家境普通，没什么背景，就提出到别的城市去闯一闯，看一看，D小姐二话没说，打包了行李就跟他走了。一晃几年过去，有一次我途经他 们共同生活工作的城市，看望他们并在他家小住。D小姐的变化令我吃惊，从前是个还有些婴儿肥的毛丫头，现在已经出落得亭亭玉立，颇有女人味了。S君的变化 到不大，还是一副刚出校门时的朴素的学生的模样。后来我离开他们家，我朋友的司机开车过来接我，他们两个陪着我等车。等我上了车，司机小哥就跟我说，那姑 娘挺漂亮，旁边是她男朋友？我说是啊，小哥就说，不咋般配啊。我说，两个人感情好着呢。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; widows: auto; max-width: 100%; clear: both; min-height: 1em; white-space: pre-wrap; color: rgb(62, 62, 62); font-family: &amp;#39;Helvetica Neue&amp;#39;, Helvetica, &amp;#39;Hiragino Sans GB&amp;#39;, &amp;#39;Microsoft YaHei&amp;#39;, Arial, sans-serif; line-height: 25.6px; box-sizing: border-box !important; word-wrap: break-word !important; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br style=&quot;margin: 0px; padding: 0px; max-width: 100%; white-space: normal; box-sizing: border-box !important; word-wrap: break-word !important;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; white-space: normal; widows: auto; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); font-family: &amp;#39;Helvetica Neue&amp;#39;, Helvetica, &amp;#39;Hiragino Sans GB&amp;#39;, &amp;#39;Microsoft YaHei&amp;#39;, Arial, sans-serif; line-height: 2em; box-sizing: border-box !important; word-wrap: break-word !important; background-color: rgb(255, 255, 255);&quot;&gt;当 然了，在一起过日子，鸡毛蒜皮的琐事，性格上的差异对感情的磨损怎么可能没有呢。但是这些我们就不大清楚了，估计也跟大多数情侣一样，可以想象得到。又过 了几年，他们所在城市的发展也不看好，于是双双回到了原来的城市。大家各忙各的，彼此都没时间说话，有一天S君在网上叫住我，我俩聊了聊，才知道他和D小 姐已经分手了。作为老朋友和情感见证人，问问为什么也不是过分的事，S君给我讲了他们后期在外地以及回来后的事情。情况很复杂，主要是两家房子、经济上的 问题，我感到很惋惜。可是S君家确实不富裕，这也是个客观事实，而且D小姐也许久不出现，许是再闭关，所以多说无益，大家就又聊了几句别的，各自散去。&lt;br style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;&quot;/&gt;&lt;br style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;&quot;/&gt;终 于有一天，我和D小姐在一起吃饭，只有我们两个人，就顺理成章地聊起这个事来。D小姐只是轻描淡写地说了一下，大概是D小姐家后来突遭巨大变故，当然这里 也有经济的问题，D小姐独自一人回来处理一切，S君此时表现就有点孩子气，给的支持呢，有点不那么给力。这件事再加上后面发生的一些事促使D小姐开始下了 决心离开，因为她……实在太伤心了。我说，我还以为是经济问题。D小姐说，他如果觉得是经济问题，那就随他吧。&lt;br style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;&quot;/&gt;&lt;br style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;&quot;/&gt;因 为D小姐不想再谈论此事，我也就没有再跟人提过，再后来有一次，是很多老朋友一起吃饭，S君来了，D小姐没在，大家都知道S君还对D小姐心有念念，就七嘴 八舌地审讯起S君来，觉得这样放弃了太可惜，也许大家可以帮着撮合撮合，只有我这个知情的人，反倒没吭声。大家都追问，到底为什么两个人会分手。S君被追 问得尴尬，只好笼统地回答，因为经济问题。令我没想到的是，我以为大家就会淡淡地安慰几句就算了，然而一桌子的男男女女，老老少少，都开始数落起S君。女 孩子说D小姐不是那种人，男生说肯定不是钱的事儿。一个老大哥给出的话最一语中的，他说我不知道你们之间交往的那些细节，但是一个姑娘，把她最好的几年都 给了你，什么都给了你，跟你去流浪，当初你就穷，人家也跟你了，最后跟你分手，你如果还认为这是因为经济问题，那就是你有问题了！我听了这话，当时真的是 太爱我的这些朋友们了。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; white-space: normal; widows: auto; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); font-family: &amp;#39;Helvetica Neue&amp;#39;, Helvetica, &amp;#39;Hiragino Sans GB&amp;#39;, &amp;#39;Microsoft YaHei&amp;#39;, Arial, sans-serif; line-height: 2em; box-sizing: border-box !important; word-wrap: break-word !important; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br style=&quot;box-sizing: border-box; margin: 0px; padding: 0px;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; white-space: normal; widows: auto; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); font-family: &amp;#39;Helvetica Neue&amp;#39;, Helvetica, &amp;#39;Hiragino Sans GB&amp;#39;, &amp;#39;Microsoft YaHei&amp;#39;, Arial, sans-serif; line-height: 2em; box-sizing: border-box !important; word-wrap: break-word !important; background-color: rgb(255, 255, 255);&quot;&gt;S君知道我和D小姐见过面，就悄悄地问我，D小姐是不是在交往新男朋友了，我说是的，S君就说，那我就明白了。唉，他又明白了，可是，他明白个P啊。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; white-space: normal; widows: auto; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); font-family: &amp;#39;Helvetica Neue&amp;#39;, Helvetica, &amp;#39;Hiragino Sans GB&amp;#39;, &amp;#39;Microsoft YaHei&amp;#39;, Arial, sans-serif; line-height: 2em; box-sizing: border-box !important; word-wrap: break-word !important; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; white-space: normal; widows: auto; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); font-family: &amp;#39;Helvetica Neue&amp;#39;, Helvetica, &amp;#39;Hiragino Sans GB&amp;#39;, &amp;#39;Microsoft YaHei&amp;#39;, Arial, sans-serif; line-height: 2em; box-sizing: border-box !important; word-wrap: break-word !important; background-color: rgb(255, 255, 255);&quot;&gt;D小姐从大概23岁的时候跟着S君，奔走天涯，漂泊异乡，租小破房子住，S君偶尔失业的时候，也义无反顾地赚钱养家，到分手的时候，历经四五年，她已经快三十了。她不是为了要去坐在宝马车里哭才离开的。&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; widows: auto; max-width: 100%; clear: both; min-height: 1em; white-space: pre-wrap; color: rgb(62, 62, 62); font-family: &amp;#39;Helvetica Neue&amp;#39;, Helvetica, &amp;#39;Hiragino Sans GB&amp;#39;, &amp;#39;Microsoft YaHei&amp;#39;, Arial, sans-serif; line-height: 25.6px; box-sizing: border-box !important; word-wrap: break-word !important; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br style=&quot;margin: 0px; padding: 0px; max-width: 100%; white-space: normal; box-sizing: border-box !important; word-wrap: break-word !important;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; white-space: normal; widows: auto; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); font-family: &amp;#39;Helvetica Neue&amp;#39;, Helvetica, &amp;#39;Hiragino Sans GB&amp;#39;, &amp;#39;Microsoft YaHei&amp;#39;, Arial, sans-serif; line-height: 2em; box-sizing: border-box !important; word-wrap: break-word !important; background-color: rgb(255, 255, 255);&quot;&gt;当 姑娘在公司受到上司无理的责难需要安慰的时候，当姑娘对于职业选择犹豫不决需要意见的时候，当姑娘辛苦了一天拖着疲惫的身子回家的时候，当姑娘给你发短信 说感冒生病的时候。我的一些男性朋友在干什么呢？他们只会安慰说，哎呀，小事情，乖，想开一点嘛，然后继续专心的看他的英超联赛。只会说，新人都是这个样 子嘛，累一点多正常。忍一忍就好了。老婆，你昨天做的那个菜很好吃哎，今天再给我做一个吧。只会告诉她，听你的，你的选着一定是对的，然后转过身盯着屏 幕。只会说出那句已经屡试不爽的黄金万能句，宝贝，多喝点儿热水，然后冲着YY大喊，治疗治疗，我靠，开大啊，你的大招呢。 而一些所谓的高帅富在干什么呢？他们会在姑娘辛苦了一天疲惫不堪的时候，对她说，累了一天啊，走，今天咱们出去吃，好好犒劳你一下。他们会在姑娘对于职业 选择犹豫不决需要意见的时候，能够沉静的听你倾诉，温和、理性的给出自己的意见，即使可能最后还是得姑娘自己拿主意。在姑娘被上司无理责难需要安慰的时 候，他们会给她一个温暖的怀抱，一个可以依靠的肩膀。&lt;br style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;section label=&quot;Copyright ? 2014 playhudong All Rights Reserved.&quot; style=&quot;margin: 10px 0px 0px; padding: 0px; white-space: normal; widows: auto; max-width: 100%; color: rgb(62, 62, 62); font-family: &amp;#39;Helvetica Neue&amp;#39;, Helvetica, &amp;#39;Hiragino Sans GB&amp;#39;, &amp;#39;Microsoft YaHei&amp;#39;, Arial, sans-serif; line-height: 25.6px; border: 1px solid rgb(78, 153, 223); border-radius: 10px; overflow: hidden; box-sizing: border-box !important; word-wrap: break-word !important; background-color: rgb(255, 255, 255);&quot;&gt;&lt;section style=&quot;margin: 0px; padding: 8px 16px; max-width: 100%; border-radius: 8px 8px 0px 0px; color: rgb(255, 255, 255); box-sizing: border-box !important; word-wrap: break-word !important; background-color: rgb(78, 153, 223);&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;如果你是姑娘，你会选择谁？&lt;/strong&gt;&lt;/section&gt;&lt;section style=&quot;margin: 0px; padding: 16px; max-width: 100%; line-height: 1.4; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-size: 14px; max-width: 100%; clear: both; min-height: 1em; white-space: pre-wrap; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; line-height: 32px; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;实际上，一些人真的遇到了一个不嫌贫爱富的好姑娘的时候，却不懂得怎么珍惜。最后一归结呢，就都觉得是经济原因，全世界的姑娘都是嫌贫爱富。现在虽然的确是有很多姑娘宁可坐在宝马车里哭，但其实依然有很多姑娘是愿意和你在自行车后座上笑。如果她有一天离开了你的自行车，是因为你把她弄哭了。 眼泪流干了的时候，人就再也留不住了…...&lt;/span&gt;&lt;/p&gt;&lt;/section&gt;&lt;/section&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; white-space: normal; widows: auto; max-width: 100%; clear: both; min-height: 1em; color: rgb(62, 62, 62); font-family: &amp;#39;Helvetica Neue&amp;#39;, Helvetica, &amp;#39;Hiragino Sans GB&amp;#39;, &amp;#39;Microsoft YaHei&amp;#39;, Arial, sans-serif; line-height: 2em; box-sizing: border-box !important; word-wrap: break-word !important; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;&quot;/&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; line-height: 2em; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;辩论不是目的，只是觉得如果用另外一种方式，换个角度看待问题，很多爱情，也许可以走到最后。 衷心祝福男孩女孩，懂得珍惜，最后得到幸福……&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img alt=&quot;白俊遥博客&quot; src=&quot;/Upload/ueditor/image/20170109/1483944456382251.png&quot; title=&quot;白俊遥博客&quot; style=&quot;box-sizing: border-box; margin: 0px; padding: 0px; border: none; vertical-align: middle;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;box-sizing: border-box; margin-top: 0px; margin-bottom: 10px; padding: 0px; font-size: 14px; color: rgb(51, 51, 51); font-family: &amp;#39;Microsoft Yahei&amp;#39;, Helvetica, Arial, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;本文转载自csdn博客：http://blog.csdn.net/gao_chun/article/details/46235027&lt;/p&gt;', '那个和你一起吃路边摊的姑娘为什么没有陪你走到最后', '那个和你一起吃路边摊的姑娘为什么没有陪你走到最后', '20');
INSERT INTO `article_data` VALUES ('&lt;p style=&quot;padding: 0px; color: rgb(51, 51, 51); font-family: &amp;quot;Microsoft Yahei&amp;quot;, Helvetica, Arial, sans-serif; font-size: 14px; margin-top: 0px; margin-bottom: 10px; white-space: normal; box-sizing: border-box; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px; box-sizing: border-box;&quot;&gt;一、nginx开启stub_status模块&lt;/strong&gt;&lt;br style=&quot;margin: 0px; padding: 0px; box-sizing: border-box;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; color: rgb(51, 51, 51); font-family: &amp;quot;Microsoft Yahei&amp;quot;, Helvetica, Arial, sans-serif; font-size: 14px; margin-top: 0px; margin-bottom: 10px; white-space: normal; box-sizing: border-box; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px; box-sizing: border-box;&quot;&gt;&amp;nbsp; 1、添加stub_status模块&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; color: rgb(51, 51, 51); font-family: &amp;quot;Microsoft Yahei&amp;quot;, Helvetica, Arial, sans-serif; font-size: 14px; margin-top: 0px; margin-bottom: 10px; white-space: normal; box-sizing: border-box; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px; box-sizing: border-box;&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/strong&gt;在编译安装nginx的时候添加“&lt;span style=&quot;margin: 0px; padding: 0px; font-family: &amp;quot;Times New Roman&amp;quot;, serif; box-sizing: border-box;&quot;&gt;--with-http_stub_status_module&lt;/span&gt;”模块。&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot; style=&quot;padding: 9.5px; border-radius: 4px; border: 1px solid rgb(204, 204, 204); border-image: none; color: rgb(51, 51, 51); line-height: 1.4285; overflow: auto; font-family: Menlo, Monaco, Consolas, &amp;quot;Courier New&amp;quot;, monospace; font-size: 13px; margin-top: 0px; margin-bottom: 10px; -ms-word-break: break-all; -ms-word-wrap: break-word; box-sizing: border-box; background-color: rgb(245, 245, 245);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;./configure&amp;nbsp;--prefix=/usr/local/nginx&amp;nbsp;--with-http_stub_status_module&lt;/pre&gt;&lt;p style=&quot;padding: 0px; color: rgb(51, 51, 51); font-family: &amp;quot;Microsoft Yahei&amp;quot;, Helvetica, Arial, sans-serif; font-size: 14px; margin-top: 0px; margin-bottom: 10px; white-space: normal; box-sizing: border-box; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&lt;span style=&quot;margin: 0px; padding: 0px; font-family: &amp;quot;Times New Roman&amp;quot;, serif; font-size: 16px; box-sizing: border-box;&quot;&gt;&lt;/span&gt;&lt;br style=&quot;margin: 0px; padding: 0px; box-sizing: border-box;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; color: rgb(51, 51, 51); font-family: &amp;quot;Microsoft Yahei&amp;quot;, Helvetica, Arial, sans-serif; font-size: 14px; margin-top: 0px; margin-bottom: 10px; white-space: normal; box-sizing: border-box; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;strong style=&quot;margin: 0px; padding: 0px; box-sizing: border-box;&quot;&gt;2、nginx配置文件中开启status&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; color: rgb(51, 51, 51); font-family: &amp;quot;Microsoft Yahei&amp;quot;, Helvetica, Arial, sans-serif; font-size: 14px; margin-top: 0px; margin-bottom: 10px; white-space: normal; box-sizing: border-box; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px; box-sizing: border-box;&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/strong&gt;在站点server下添加一个location&lt;strong style=&quot;margin: 0px; padding: 0px; box-sizing: border-box;&quot;&gt;。&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot; style=&quot;padding: 9.5px; border-radius: 4px; border: 1px solid rgb(204, 204, 204); border-image: none; color: rgb(51, 51, 51); line-height: 1.4285; overflow: auto; font-family: Menlo, Monaco, Consolas, &amp;quot;Courier New&amp;quot;, monospace; font-size: 13px; margin-top: 0px; margin-bottom: 10px; -ms-word-break: break-all; -ms-word-wrap: break-word; box-sizing: border-box; background-color: rgb(245, 245, 245);&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;server&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;listen&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;80;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;server_name&amp;nbsp;&amp;nbsp;www.xxx.com;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;location&amp;nbsp;/&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;root&amp;nbsp;html;\r\n&amp;nbsp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;location&amp;nbsp;/ngxstatus&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;stub_status;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;access_log&amp;nbsp;off;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;allow&amp;nbsp;127.0.0.1\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;allow&amp;nbsp;192.168.0.0/24;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;deny&amp;nbsp;all;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&lt;/pre&gt;&lt;p style=&quot;padding: 0px; color: rgb(51, 51, 51); font-family: &amp;quot;Microsoft Yahei&amp;quot;, Helvetica, Arial, sans-serif; font-size: 14px; margin-top: 0px; margin-bottom: 10px; white-space: normal; box-sizing: border-box; background-color: rgb(255, 255, 255);&quot;&gt;&lt;br style=&quot;margin: 0px; padding: 0px; box-sizing: border-box;&quot;/&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; color: rgb(51, 51, 51); font-family: &amp;quot;Microsoft Yahei&amp;quot;, Helvetica, Arial, sans-serif; font-size: 14px; margin-top: 0px; margin-bottom: 10px; white-space: normal; box-sizing: border-box; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px; box-sizing: border-box;&quot;&gt;二、编写脚本获取nginx status&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;padding: 0px; color: rgb(51, 51, 51); font-family: &amp;quot;Microsoft Yahei&amp;quot;, Helvetica, Arial, sans-serif; font-size: 14px; margin-top: 0px; margin-bottom: 10px; white-space: normal; box-sizing: border-box; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px; box-sizing: border-box;&quot;&gt;&amp;nbsp; 1、脚本&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot; style=&quot;padding: 9.5px; border-radius: 4px; border: 1px solid rgb(204, 204, 204); border-image: none; color: rgb(51, 51, 51); line-height: 1.4285; overflow: auto; font-family: Menlo, Monaco, Consolas, &amp;quot;Courier New&amp;quot;, monospace; font-size: 13px; margin-top: 0px; margin-bottom: 10px; -ms-word-break: break-all; -ms-word-wrap: break-word; box-sizing: border-box; background-color: rgb(245, 245, 245);&quot;&gt;&amp;nbsp;#!/bin/bash\r\n&amp;nbsp;&amp;nbsp;nginx_active(){\r\n&amp;nbsp;&amp;nbsp;/usr/bin/curl&amp;nbsp;&amp;quot;http://127.0.0.1/ngxstatus&amp;quot;&amp;nbsp;2&amp;gt;/dev/null|&amp;nbsp;grep&amp;nbsp;&amp;#39;Active&amp;#39;&amp;nbsp;|&amp;nbsp;awk&amp;nbsp;&amp;#39;{print&amp;nbsp;$NF}&amp;#39;\r\n&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;nginx_reading(){\r\n&amp;nbsp;&amp;nbsp;/usr/bin/curl&amp;nbsp;&amp;quot;http://127.0.0.1/ngxstatus&amp;quot;&amp;nbsp;2&amp;gt;/dev/null|&amp;nbsp;grep&amp;nbsp;&amp;#39;Reading&amp;#39;&amp;nbsp;|&amp;nbsp;awk&amp;nbsp;&amp;#39;{print&amp;nbsp;$2}&amp;#39;\r\n&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;nginx_writing(){\r\n&amp;nbsp;&amp;nbsp;/usr/bin/curl&amp;nbsp;&amp;quot;http://127.0.0.1/ngxstatus&amp;quot;&amp;nbsp;2&amp;gt;/dev/null|&amp;nbsp;grep&amp;nbsp;&amp;#39;Writing&amp;#39;&amp;nbsp;|&amp;nbsp;awk&amp;nbsp;&amp;#39;{print&amp;nbsp;$4}&amp;#39;\r\n&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;nginx_waiting(){\r\n&amp;nbsp;&amp;nbsp;/usr/bin/curl&amp;nbsp;&amp;quot;http://127.0.0.1/ngxstatus&amp;quot;&amp;nbsp;2&amp;gt;/dev/null|&amp;nbsp;grep&amp;nbsp;&amp;#39;Waiting&amp;#39;&amp;nbsp;|&amp;nbsp;awk&amp;nbsp;&amp;#39;{print&amp;nbsp;$6}&amp;#39;\r\n&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;nginx_accepts(){\r\n&amp;nbsp;&amp;nbsp;/usr/bin/curl&amp;nbsp;&amp;quot;http://127.0.0.1/ngxstatus&amp;quot;&amp;nbsp;2&amp;gt;/dev/null|&amp;nbsp;awk&amp;nbsp;&amp;#39;NR==3{print&amp;nbsp;$1}&amp;#39;\r\n&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;nginx_handled(){\r\n&amp;nbsp;&amp;nbsp;/usr/bin/curl&amp;nbsp;&amp;quot;http://127.0.0.1/ngxstatus&amp;quot;&amp;nbsp;2&amp;gt;/dev/null|&amp;nbsp;awk&amp;nbsp;&amp;#39;NR==3{print&amp;nbsp;$2}&amp;#39;\r\n&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;nginx_requests(){\r\n&amp;nbsp;&amp;nbsp;/usr/bin/curl&amp;nbsp;&amp;quot;http://127.0.0.1/ngxstatus&amp;quot;&amp;nbsp;2&amp;gt;/dev/null|&amp;nbsp;awk&amp;nbsp;&amp;#39;NR==3{print&amp;nbsp;$3}&amp;#39;\r\n&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;case&amp;nbsp;$1&amp;nbsp;in\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;active)\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;nginx_active\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;;;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbs